--SELECT COUNT(t0.productid) FROM product t0, productvariant t1 WHERE ((((((t0.productName LIKE ? OR t0.keywords LIKE ?) OR t0.model LIKE ?) OR t0.description LIKE ?) OR t1.color LIKE ?) OR t1.productSize LIKE ?) OR t1.weight LIKE ?)

SELECT DISTINCT t1.productid AS a1, t1.dateOfManufacture AS a2, t1.datecreated AS a3, t1.description AS a4, t1.discount AS a5, t1.keywords AS a6, t1.logo AS a7, t1.minimumOrderQuantity AS a8, t1.model AS a9, t1.price AS a10, t1.productName AS a11, t1.ratingPercent AS a12, t1.timemodified AS a13, t1.url AS a14, t1.validFrom AS a15, t1.validThrough AS a16, t1.views AS a17, t1.availabilityid AS a18, t1.availableAtOrFrom AS a19, t1.brandid AS a20, t1.currencyid AS a21, t1.isRelatedTo AS a22, t1.productcategoryid AS a23, t1.productstatusid AS a24, t1.productsubcategoryid AS a25, t1.seller AS a26 FROM productvariant t0, productvariant t2, product t1 WHERE (((((((t1.productName LIKE ? OR t1.keywords LIKE ?) OR t1.model LIKE ?) OR t1.description LIKE ?) OR t2.color LIKE ?) OR t2.productSize LIKE ?) OR t2.weight LIKE ?) AND (t0.productid = t1.productid)) ORDER BY t1.price ASC LIMIT ?, ?